// https://leetcode.com/problems/longest-palindromic-substring/
// 받은 문자열 내에서 거꾸로해도 제일 긴 substr을 찾는 문제 (보류)
/* 
""
테스트케이스.. 요고 통과 안댐
*/

/**
 * @param {string} s
 * @return {string}
 */
const longestPalindrome = (s) => {
    const arrResult = [];

    let nLoopCnt = 0;
    let lastIdx = nLoopCnt + 1;
    while (nLoopCnt !== s.length) {
        const currSubStr = s.substring(nLoopCnt, lastIdx);

        if (arrResult.indexOf(currSubStr) <= -1) arrResult.push(currSubStr);

        if (lastIdx >= s.length) {
            nLoopCnt++;
            lastIdx = nLoopCnt + 1;
        } else lastIdx++;
    }
    const result = arrResult
        .sort((a, b) => b.length - a.length)
        .filter((v) => [...v].reverse().join('') === v);

    return result[0];
};

longestPalindrome('');
